    private function Category(string $slug): string
    {
        $category = Category::where('slug', $slug)->first();
        if (!$category) {
            $category = Category::create([
                'slug' => $slug,
                'name' => ucfirst($slug),
            ]);
        }
        return $category->id;
    }
